style:
.fos-hero {
  margin: 8px 0 12px;
  padding: 18px 22px;
  border-radius: 10px;
  background: #ffffff;
  box-shadow: 0 1px 3px rgba(15, 23, 42, 0.04);
  border: 1px solid #e5e7eb;
  font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
  display: flex;
  align-items: stretch;
}

.fos-hero-left {
  flex: 1;
  min-width: 0;
}

/* Chip */

.fos-chip {
  display: inline-flex;
  align-items: center;
  gap: 6px;
  padding: 3px 10px;
  border-radius: 999px;
  background: #f3f4f6;
  border: 1px solid #e5e7eb;
  font-size: 11px;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.06em;
  color: #4b5563;
  margin-bottom: 6px;
}

.fos-chip-dot {
  width: 6px;
  height: 6px;
  border-radius: 999px;
  background: #f97316;
}

/* Heading */

.fos-title {
  font-size: 20px;
  font-weight: 600;
  color: #111827;
  margin: 0 0 4px;
}

.fos-subtitle {
  font-size: 13px;
  color: #6b7280;
  margin-bottom: 14px;
}

/* Flow header */

.fos-flow-header {
  display: flex;
  align-items: baseline;
  gap: 8px;
  margin-bottom: 10px;
}

.fos-flow-label {
  font-size: 11px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.08em;
  color: #6b7280;
}

.fos-flow-hint {
  font-size: 11px;
  color: #9ca3af;
}

/* Steps */

.fos-flow-grid {
  display: grid;
  grid-template-columns: repeat(2, minmax(0, 1fr));
  gap: 10px 16px;
}

.fos-flow-step {
  display: flex;
  align-items: flex-start;
  gap: 8px;
  padding: 8px 10px;
  border-radius: 999px;
  border: 1px solid #e5e7eb;
  background: #ffffff;
}

.fos-step-circle {
  width: 22px;
  height: 22px;
  border-radius: 999px;
  background: #eff6ff;
  color: #1d4ed8;
  font-size: 12px;
  font-weight: 600;
  display: flex;
  align-items: center;
  justify-content: center;
  border: 1px solid #bfdbfe;
  flex-shrink: 0;
}

.fos-step-text {
  min-width: 0;
}

.fos-step-title {
  font-size: 12px;
  font-weight: 600;
  color: #111827;
}

.fos-step-sub {
  font-size: 11px;
  color: #6b7280;
}

/* Footer note */

.fos-footer-note {
  margin-top: 10px;
  font-size: 11px;
  color: #059669;
  padding: 6px 8px;
  border-radius: 6px;
  background: #ecfdf3;
  border: 1px dashed #a7f3d0;
}

/* Responsive */

@media (max-width: 768px) {
  .fos-hero {
    padding: 16px 14px;
  }

  .fos-flow-grid {
    grid-template-columns: minmax(0, 1fr);
  }
}
